import { useState } from "react";
import "./App.css";
import { toBin, toDec } from "./utils/binary";

function App() {
  const [A, setA] = useState("");
  const [B, setB] = useState("");
  return (
    <div className="App">
      <header>
        <h1>Sistemas digitais - EL2 é a melhor turma</h1>
      </header>
      Va:
      <input
        type="text"
        value={A}
        onChange={(e) => {
          const { data, inputType } = e.nativeEvent;
          if (inputType === "deleteContentBackward") {
            setA(A.slice(0, A.length - 1));
          }
          if (data === "0" || data === "1") {
            setA(A + data);
          }
        }}
      />
      Vb:
      <input
        type="text"
        value={B}
        onChange={(e) => {
          const { data, inputType } = e.nativeEvent;
          if (inputType === "deleteContentBackward") {
            setB(B.slice(0, B.length - 1));
          }
          if (data === "0" || data === "1") {
            setB(B + data);
          }
        }}
      />
      <table>
        <tr>
          <th></th>
          <th>RESULTADO</th>
          <th>C/B</th>
          <th>OV</th>
          <th>SINAL</th>
          <th>PAR</th>
          <th>ZERO</th>
        </tr>
        <tr>
          <td>Va + Vb</td>
          <td>{toBin(toDec(A) + toDec(B), A.length)}</td>
        </tr>
        <tr>
          <td>Va - Vb</td>
        </tr>
        <tr>
          <td>Va x Vb</td>
        </tr>
        <tr>
          <td>Va x Vb</td>
        </tr>
        <tr>
          <td>Va / Vb</td>
        </tr>
        <tr>
          <td>Va / Vb</td>
        </tr>
        <tr>
          <td>Va AND Vb</td>
        </tr>
        <tr>
          <td>Va OR Vb</td>
        </tr>
        <tr>
          <td>Va XOR Vb</td>
        </tr>
      </table>
    </div>
  );
}

export default App;
