export const toBin = (value, bits) => {
  const bin = value.toString(2);
  if (bin.length >= bits) {
    return bin.slice(bin.length - bits, bin.length);
  } else {
    return Array(bits - bin.length).join("0") + bin;
  }
};

export const toDec = (value) => parseInt(value, 2);

export const binMask = ({ data, inputType }, value) => {
  if (inputType === "deleteContentBackward") {
    return value.slice(0, value.length - 1);
  }
  if (data === "0" || data === "1") {
    return value + data;
  }
};
